// src/pages/ResumeAnalyzer.jsx
import React, { useState } from "react";
import { api } from "../api";

const ResumeAnalyzer = () => {
  const [file, setFile] = useState(null);
  const [jobTitle, setJobTitle] = useState("Full Stack Developer");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setResult(null);
    setError("");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    if (!file) {
      setError("Please select a resume file.");
      return;
    }

    const formData = new FormData();
    formData.append("resume", file);
    formData.append("jobTitle", jobTitle);

    try {
      setLoading(true);
      const res = await api.post("/api/resume/analyze", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setResult(res.data.analysis);
    } catch (err) {
      console.error(err);
      setError(
        err?.response?.data?.message || "Failed to analyze resume. Try again."
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="page">
      <h2>Resume ATS Analyzer</h2>
      <p className="muted">
        Upload your <strong>PDF</strong> or <strong>DOCX</strong> resume to get
        an estimated ATS score and improvement suggestions.
      </p>

      <div className="card">
        {error && <div className="error-box">{error}</div>}
        <form onSubmit={handleSubmit} className="form">
          <label>
            Target Job Title
            <input
              type="text"
              value={jobTitle}
              onChange={(e) => setJobTitle(e.target.value)}
              placeholder="Full Stack Developer"
            />
          </label>
          <label className="file-label">
            Resume File (PDF / DOCX)
            <input type="file" accept=".pdf,.docx" onChange={handleFileChange} />
          </label>
          {file && (
            <p className="muted small-text">
              Selected: <strong>{file.name}</strong>
            </p>
          )}
          <button className="btn-primary" disabled={loading}>
            {loading ? "Analyzing..." : "Analyze Resume"}
          </button>
        </form>
      </div>

      {result && (
        <div className="card result-card">
          <h3>Result</h3>
          <div className="score-display">
            <div className="score-circle">
              <span>{result.score}</span>
              <small>/ 100</small>
            </div>
            <div className="score-details">
              <p>
                <strong>Job Title:</strong> {result.jobTitle}
              </p>
              <p className="muted small-text">
                File: {result.originalFileName} ({result.fileType})
              </p>
              {result.breakdown && (
                <ul className="small-text">
                  <li>
                    Keywords Score: {result.breakdown.keywordScore}/60
                  </li>
                  <li>Length Score: {result.breakdown.lengthScore}/25</li>
                  <li>
                    Structure Score: {result.breakdown.structureScore}/15
                  </li>
                </ul>
              )}
            </div>
          </div>
          {result.suggestions && result.suggestions.length > 0 && (
            <>
              <h4>Suggestions</h4>
              <ul className="suggestions-list">
                {result.suggestions.map((s, i) => (
                  <li key={i}>{s}</li>
                ))}
              </ul>
            </>
          )}
        </div>
      )}
    </div>
  );
};

export default ResumeAnalyzer;
